// Filter for invoice emails
const email = $input.item.json;

// Check if email likely contains an invoice
const subjectLower = (email.subject || '').toLowerCase();
const hasInvoiceKeyword = ['invoice', 'inv', 'bill', 'payment', 'receipt'].some(
  keyword => subjectLower.includes(keyword)
);

// Check for attachments
const hasAttachments = email.attachments && email.attachments.length > 0;
const hasPdfAttachment = hasAttachments && email.attachments.some(
  att => att.filename && att.filename.toLowerCase().endsWith('.pdf')
);

if (hasInvoiceKeyword && hasPdfAttachment) {
  return {
    json: {
      ...email,
      isInvoiceEmail: true,
      pdfAttachments: email.attachments.filter(
        att => att.filename && att.filename.toLowerCase().endsWith('.pdf')
      )
    }
  };
}

// Skip non-invoice emails
return null;

// Parse OCR response and extract invoice data
const ocrResponse = $input.item.json;

// Helper function to find field value
function findField(fields, patterns) {
  for (const field of fields || []) {
    const label = (field.Type?.Text || '').toLowerCase();
    for (const pattern of patterns) {
      if (label.includes(pattern)) {
        return field.ValueDetection?.Text || field.Value?.Text;
      }
    }
  }
  return null;
}

// Extract key invoice fields
const summaryFields = ocrResponse.ExpenseDocuments?.[0]?.SummaryFields || [];

const invoiceData = {
  invoiceNumber: findField(summaryFields, ['invoice', 'inv #', 'invoice number']),
  vendor: findField(summaryFields, ['vendor', 'supplier', 'from', 'company']),
  date: findField(summaryFields, ['date', 'invoice date']),
  dueDate: findField(summaryFields, ['due date', 'payment due', 'due']),
  subtotal: parseFloat((findField(summaryFields, ['subtotal', 'sub total']) || '0').replace(/[^0-9.]/g, '')),
  tax: parseFloat((findField(summaryFields, ['tax', 'vat', 'gst']) || '0').replace(/[^0-9.]/g, '')),
  total: parseFloat((findField(summaryFields, ['total', 'amount due', 'balance']) || '0').replace(/[^0-9.]/g, '')),
  currency: findField(summaryFields, ['currency']) || 'USD'
};

// Extract line items
const lineItemGroups = ocrResponse.ExpenseDocuments?.[0]?.LineItemGroups || [];
const lineItems = [];

for (const group of lineItemGroups) {
  for (const lineItem of group.LineItems || []) {
    const item = {};
    for (const field of lineItem.LineItemExpenseFields || []) {
      const type = field.Type?.Text?.toLowerCase();
      const value = field.ValueDetection?.Text;

      if (type?.includes('description') || type?.includes('item')) {
        item.description = value;
      } else if (type?.includes('quantity') || type?.includes('qty')) {
        item.quantity = parseFloat(value) || 1;
      } else if (type?.includes('price') || type?.includes('rate')) {
        item.unitPrice = parseFloat((value || '0').replace(/[^0-9.]/g, ''));
      } else if (type?.includes('amount') || type?.includes('total')) {
        item.amount = parseFloat((value || '0').replace(/[^0-9.]/g, ''));
      }
    }
    if (item.description) {
      lineItems.push(item);
    }
  }
}

invoiceData.lineItems = lineItems;
invoiceData.confidence = ocrResponse.ExpenseDocuments?.[0]?.Blocks?.[0]?.Confidence || 0;
invoiceData.processedAt = new Date().toISOString();

return { json: invoiceData };

// Validate invoice data
const invoice = $input.item.json;

const errors = [];
const warnings = [];

// Required field validation
if (!invoice.invoiceNumber) {
  errors.push('Missing invoice number');
}
if (!invoice.vendor) {
  errors.push('Missing vendor information');
}
if (!invoice.total || invoice.total <= 0) {
  errors.push('Invalid or missing total amount');
}
if (!invoice.date) {
  warnings.push('Missing invoice date');
}

// Business rule validation
if (invoice.total > 10000) {
  warnings.push('High value invoice - requires manager approval');
}

// Date validation
if (invoice.dueDate) {
  const dueDate = new Date(invoice.dueDate);
  const today = new Date();
  const daysUntilDue = Math.ceil((dueDate - today) / (1000 * 60 * 60 * 24));

  if (daysUntilDue < 0) {
    warnings.push(`Invoice is ${Math.abs(daysUntilDue)} days overdue`);
  } else if (daysUntilDue <= 7) {
    warnings.push(`Invoice due in ${daysUntilDue} days`);
  }

  invoice.daysUntilDue = daysUntilDue;
}

// Confidence check
if (invoice.confidence < 80) {
  warnings.push('Low OCR confidence - manual review recommended');
}

// Calculate validation result
const isValid = errors.length === 0;
const requiresApproval = warnings.length > 0 || invoice.total > 5000;

return {
  json: {
    ...invoice,
    validation: {
      isValid,
      errors,
      warnings,
      requiresApproval
    }
  }
};

