// Generate daily financial summary
const fs = require('fs');
const path = require('path');

const invoicesDir = '/data/invoices';
const today = new Date();

// Read all invoice files
let invoices = [];
try {
  const files = fs.readdirSync(invoicesDir);
  for (const file of files) {
    if (file.endsWith('.json')) {
      const content = fs.readFileSync(path.join(invoicesDir, file), 'utf8');
      invoices.push(JSON.parse(content));
    }
  }
} catch (error) {
  // Directory may not exist yet
}

// Calculate summary metrics
const summary = {
  date: today.toISOString().split('T')[0],
  totalInvoices: invoices.length,
  totalAmount: invoices.reduce((sum, inv) => sum + (inv.total || 0), 0),
  unpaidInvoices: invoices.filter(inv => !inv.paidDate).length,
  unpaidAmount: invoices.filter(inv => !inv.paidDate).reduce((sum, inv) => sum + (inv.total || 0), 0),
  overdueInvoices: [],
  dueSoonInvoices: [],
  byVendor: {},
  byCategory: {}
};

// Categorize invoices
for (const invoice of invoices) {
  if (!invoice.paidDate && invoice.dueDate) {
    const dueDate = new Date(invoice.dueDate);
    const daysUntilDue = Math.ceil((dueDate - today) / (1000 * 60 * 60 * 24));

    if (daysUntilDue < 0) {
      summary.overdueInvoices.push({
        invoiceNumber: invoice.invoiceNumber,
        vendor: invoice.vendor,
        amount: invoice.total,
        daysOverdue: Math.abs(daysUntilDue)
      });
    } else if (daysUntilDue <= 7) {
      summary.dueSoonInvoices.push({
        invoiceNumber: invoice.invoiceNumber,
        vendor: invoice.vendor,
        amount: invoice.total,
        daysUntilDue
      });
    }
  }

  // Group by vendor
  const vendor = invoice.vendor || 'Unknown';
  if (!summary.byVendor[vendor]) {
    summary.byVendor[vendor] = { count: 0, total: 0 };
  }
  summary.byVendor[vendor].count++;
  summary.byVendor[vendor].total += invoice.total || 0;
}

return { json: summary };

