// Generate monthly financial report
const fs = require('fs');
const path = require('path');

const today = new Date();
const lastMonth = new Date(today.getFullYear(), today.getMonth() - 1, 1);
const monthName = lastMonth.toLocaleString('default', { month: 'long', year: 'numeric' });

const invoicesDir = '/data/invoices';
const summariesDir = '/data/financial_summaries';

// Read all invoices
let invoices = [];
try {
  const files = fs.readdirSync(invoicesDir);
  for (const file of files) {
    if (file.endsWith('.json')) {
      const content = fs.readFileSync(path.join(invoicesDir, file), 'utf8');
      invoices.push(JSON.parse(content));
    }
  }
} catch (error) {}

// Filter to last month
const monthStart = new Date(lastMonth.getFullYear(), lastMonth.getMonth(), 1);
const monthEnd = new Date(lastMonth.getFullYear(), lastMonth.getMonth() + 1, 0);

const monthlyInvoices = invoices.filter(inv => {
  const invDate = new Date(inv.date || inv.processedAt);
  return invDate >= monthStart && invDate <= monthEnd;
});

// Generate report
const report = {
  period: monthName,
  generatedAt: today.toISOString(),
  summary: {
    totalInvoices: monthlyInvoices.length,
    totalAmount: monthlyInvoices.reduce((sum, inv) => sum + (inv.total || 0), 0),
    paidInvoices: monthlyInvoices.filter(inv => inv.paidDate).length,
    paidAmount: monthlyInvoices.filter(inv => inv.paidDate).reduce((sum, inv) => sum + (inv.total || 0), 0),
    unpaidInvoices: monthlyInvoices.filter(inv => !inv.paidDate).length,
    unpaidAmount: monthlyInvoices.filter(inv => !inv.paidDate).reduce((sum, inv) => sum + (inv.total || 0), 0)
  },
  byVendor: {},
  byCategory: {},
  trends: {
    averageInvoiceAmount: monthlyInvoices.length > 0 
      ? monthlyInvoices.reduce((sum, inv) => sum + (inv.total || 0), 0) / monthlyInvoices.length 
      : 0,
    averagePaymentTime: 0 // Calculate from paid invoices
  }
};

// Group by vendor
for (const inv of monthlyInvoices) {
  const vendor = inv.vendor || 'Unknown';
  if (!report.byVendor[vendor]) {
    report.byVendor[vendor] = { count: 0, total: 0, paid: 0 };
  }
  report.byVendor[vendor].count++;
  report.byVendor[vendor].total += inv.total || 0;
  if (inv.paidDate) report.byVendor[vendor].paid += inv.total || 0;
}

return { json: report };

